import React, { useState } from 'react';
import { getContentSummary, getContentExplanation, updateProgress } from '../services/api';

function ContentFeed({ domain, content, onContentUpdate }) {
  const [expandedContent, setExpandedContent] = useState({});

  const handleSummary = async (item) => {
    try {
      const data = await getContentSummary(domain, item._id);
      setExpandedContent(prev => ({
        ...prev,
        [item._id]: { ...data, type: 'summary' }
      }));
      await updateProgress(item.title);
    } catch (error) {
      console.error('Error getting summary:', error);
    }
  };

  const handleExplanation = async (item) => {
    try {
      const data = await getContentExplanation(domain, item._id);
      setExpandedContent(prev => ({
        ...prev,
        [item._id]: { ...data, type: 'explanation' }
      }));
      await updateProgress(item.title);
    } catch (error) {
      console.error('Error getting explanation:', error);
    }
  };

  return (
    <div className="content-feed">
      <h2>{domain.charAt(0).toUpperCase() + domain.slice(1)} Content</h2>

      {content.map(item => (
        <div key={item._id} className="content-item">
          <h3>{item.title}</h3>
          <div className="content-meta">
            {item.source} â€¢ {new Date(item.publishedAt).toLocaleDateString()}
          </div>
          <p>{item.content}</p>

          {expandedContent[item._id] && (
            <div className="expanded-content">
              <h4>{expandedContent[item._id].type === 'summary' ? 'Summary' : 'Detailed Explanation'}</h4>
              <p>{expandedContent[item._id].summary || expandedContent[item._id].explanation}</p>
            </div>
          )}

          <div className="content-actions">
            <button className="btn" onClick={() => handleSummary(item)}>
              Quick Summary
            </button>
            <button className="btn" onClick={() => handleExplanation(item)}>
              Detailed Explanation
            </button>
            {item.url && (
              <a href={item.url} target="_blank" rel="noopener noreferrer" className="btn">
                Read Full Article
              </a>
            )}
          </div>
        </div>
      ))}
    </div>
  );
}

export default ContentFeed;
